import * as React from 'react';
import { View, Text, Button, StyleSheet, Image, ScrollView, Dimensions, ImageBackground, Switch, TextInput } from 'react-native';
import { useEffect, useState, useReducer } from 'react'
import { TouchableOpacity } from 'react-native-gesture-handler';
import { useSelector, useDispatch } from 'react-redux';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const styles = StyleSheet.create({
    container: 
    {
        backgroundColor: 'rgb(21, 34, 56)',
        height: windowHeight,
        width: windowWidth,
        alignItems: 'center'
    },
    textInput: 
    {
        height: 50, //to be determined,
        width: windowWidth - 30,
        backgroundColor: 'rgb(35, 57, 109)',
        borderRadius: 10,
        padding: '3%',
        color: 'white',
        fontSize: 15
    },
    button:
    {
        color: 'white',
        width: (windowWidth - 30) / 2.5,
        height: 40,
        marginTop: 40,
        backgroundColor: 'rgb(32, 51, 84)',
        borderRadius: 10,
        alignItems: 'center',
    }
})

function EditPrinterNameView(props)
{
    const [value, onChangeText] = useState('Printer Name');

    var temp;
    const listPrinters = useSelector(state => { temp = [...state.listP] ; return state.listP })
    const dispatch = useDispatch()

    //Returns the index of the printer that was passed in
    function findPrinter()
    {
        var returnValue;

        for (i = 0; i < temp.length; i++)
        {
        if (temp[i].id == props.id)
        {
            returnValue = i
            break
        }
        }
        return returnValue
    }

    //Updates the global state of a certain printer
    function updateName(newName)
    {
        var printerArray = [...listPrinters]
        let i = findPrinter()

        if (passesChecks(printerArray[i]), newName)
        {
            if (newName.length <= 20)
            {
                console.log('THIS IS PRINTER ARRAY ', i)
                printerArray[i].name = newName

                dispatch(updatePrinter(printerArray))
            }
        }
    }

    function passesChecks(printer, newName)
    {
        var returnVal = false;

        for (i = 0; i < listPrinters.length; i++)
        {
            if ((newName == listPrinters[i].name) && (printer.id != listPrinters[i].id))
            {
                returnVal = true
                break
            }
        }

        return returnVal
    }

    return(
        <View style={ styles.container }>
            <TextInput
                style={ styles.textInput }
                onChangeText={text => onChangeText(text)}
                value={value}
            />
            <TouchableOpacity style={styles.button} onPress={() => updateName(value)}>
                <Text style={{ color: 'white', marginTop: 10 }}>Update Name</Text>
            </TouchableOpacity>
        </View>
    )
}

export { EditPrinterNameView };
