import * as React from 'react';
import { View, Text, Button, StyleSheet, Image, ScrollView, Dimensions, ImageBackground, Switch, Modal } from 'react-native';
import { Divider } from './Divider.js';
import { useSelector, useDispatch } from 'react-redux';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { updatePrinter } from './updatePrinters.js';
import { useEffect, useState, useReducer } from 'react'
import ActionSheet from './ActionSheet.js';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const styles = StyleSheet.create({
    text: 
    {
        fontSize: 15, 
        color: 'gray', 
        marginLeft: 10, 
        marginBottom: 10,
    },
    backgroundColor:
    {
        width: windowWidth - 20,
        backgroundColor: 'rgb(25, 40, 65)',
        alignSelf: 'center',
        borderRadius: 10,
    },
    container: 
    {
        backgroundColor: 'rgb(21, 34, 56)',
        flex: 1,
        flexDirection: 'column'
    }
  })

function EditPrinterView({route, navigation})
{
    var temp;
    const { printer } = route.params;

    const listPrinters = useSelector(state => { temp = [...state.listP]; return state.listP })

    const [actionSheet, setActionSheet] = useState(false)
    const closeActionSheet = () => setActionSheet(false);

    const actionItems = [
        {
          id: 1,
          label: 'Action Item 1',
          onPress: () => {
          }
        },
        {
          id: 2,
          label: 'Action Item 2',
          onPress: () => {
          }
        },
        {
          id: 3,
          label: 'Action Item 3',
          onPress: () => {
          }
        },
        {
          id: 4,
          label: 'Action Item 4',
          onPress: () => {
          }
        },
      ];

    function findPrinter()
    {
        var returnVal;

        for (i = 0; i < listPrinters.length; i++)
        {
            if (printer.id == listPrinters[i].id)
            {
                returnVal = i
            }
        }

        return returnVal
    }

    let i = findPrinter()
    var displayedPrinter = listPrinters[i]

    return(
        <React.Fragment>
            <View style={ styles.container }>
                <Text style={ styles.text }>Printer Options</Text>
                <View style={ styles.backgroundColor }>
                    <TouchableOpacity style={{ flexDirection: 'row' }} onPress={() => navigation.navigate('Edit Printer Name', { printer: printer })} >
                        <Text style={{ color: 'white', fontSize: 15, marginLeft: 10, marginTop: 15, marginBottom: 10 }}>Name: { displayedPrinter.name }</Text>
                        <Text style={{ color: 'green', fontSize: 15, marginRight: 10, marginTop: 15, marginBottom: 20, position: 'absolute', right: 0 }}>icon</Text>
                    </TouchableOpacity>
                    <Divider width={windowWidth - 20}/>
                    <TouchableOpacity onPress={() => setActionSheet(true)}>
                        <Text style={{ color: 'white', fontSize: 15, marginLeft: 10, marginTop: 15, marginBottom: 10 }}>Sensitivity</Text>
                    </TouchableOpacity>
                    <Divider width={windowWidth - 20}/>
                    <SwitchView text={'Notify on failure'} printer={printer} />
                    <Divider width={windowWidth - 20}/>
                    <SwitchView text={'Pause on failure'} printer={printer}/>
                </View>
                <Modal
                    visible={actionSheet}
                    animationType='slide'
                    transparent={true}
                >
                    <ActionSheet
                        actionItems={actionItems}
                        onCancel={closeActionSheet}
                    />
                </Modal>
            </View>
        </React.Fragment>
    )
}

function SwitchView(props)
{
    var OFcodes = { 'Notify on failure':props.printer.notifyOnFail, 'Pause on failure':props.printer.pauseOnFail }

    var temp;
    const listPrinters = useSelector(state => { temp = [...state.listP]; return state.listP })
    const dispatch = useDispatch()

    const [switchBool, setBool] = useState(OFcodes[props.text])

    function findPrinterIndex()
    {
        var returnValue;

        for (i=0; i<temp.length; i++)
        {
            if (temp[i].id == props.printer.id)
            {
                returnValue = 1
                break
            }
        }

        return returnValue
    }
    //Updates the 
    function updateNOF(newValue)
    {
        var array = [...listPrinters]
        var i = findPrinterIndex()

        if (props.text == 'Notify on failure')
        {
            array[i].notifyOnFail = newValue
        }else 
        {
            array[i].pauseOnFail = newValue
        }

        dispatch(updatePrinter(array))

        setBool(newValue)
    }

    return(
        <View>
            <TouchableOpacity>
                <Text style={{ color: 'white', fontSize: 15, marginLeft: 10, marginTop: 15, marginBottom: 10 }}>{props.text}</Text>
            </TouchableOpacity>
            <Switch
                style={{position: 'absolute', right: 0, marginRight: 10, marginTop: 10 }}
                trackColor={{ false: 'gray', true: 'green' }}
                thumbColor="white"
                ios_backgroundColor="gray"
                onValueChange={(value) => { console.log('THIS IS THE VALUE ', value); updateNOF(value)}}
                value={switchBool}
            />
        </View>
    )
}

export { EditPrinterView };
