import * as React from 'react';
import { Auth } from 'aws-amplify';
import { useEffect, useState, useReducer } from 'react'
import { View, Text, Button, StyleSheet, Image, ScrollView, Dimensions, ImageBackground } from 'react-native';
import { Ionicons as Icon} from '@expo/vector-icons';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

var rectangleHeight = windowWidth / 7
var centerInRect = rectangleHeight / 3.5

const styles = StyleSheet.create({
  background:
  {
    flex: 1,
    backgroundColor:'rgb(21, 34, 56)',
    textAlign: 'left', 
  }
})

function Row(props)
{
  return(
    <View style={{
      width: windowWidth - 20,
      height: rectangleHeight,
      backgroundColor: 'rgb(25, 40, 65)',
      alignItems: 'leading',
      marginLeft: 10,
      borderRadius: 10,
      flexDirection: 'row'
    }}>
      <Icon name='md-person' size={20} color='#7CFC00' style={{ marginLeft: 10, marginTop: centerInRect, marginBottom: centerInRect }} />
      <Text style={{ color: 'white', marginLeft: 10, marginTop: centerInRect + 2.5, marginBottom: centerInRect }}>{props.Text}</Text>
      <Text style={{ position: 'absolute', right: 0, color: 'red', marginRight: 10, marginTop: centerInRect, marginBottom: centerInRect }}>Sign Out</Text>
    </View>
  )
}

function SettingsPage()
{
  const [name, setName] = useState([])

  useEffect(() => 
  {
    var test = getUsername().then(test => setName(test))
  }, [])

  async function getUsername()
  {
    var userInfo = await Auth.currentUserInfo()
    var userName = userInfo.attributes['name']
    return userName
  }

  return(
    <View style = {styles.background}>
        <Text style={{ color: 'gray', fontSize: 15, marginLeft: 10, marginBottom: 10 }}>Account Settings</Text>
        <Row Text= {name} />
    </View>
  )
}



export {SettingsPage};
