import * as React from 'react';
import { View, Text, Button, StyleSheet, Image, ScrollView, Dimensions, ImageBackground } from 'react-native';
import { CompletionView } from './CompletionView.js';


const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const CircleSize = windowWidth / (windowWidth/(windowHeight/6))

const imageHeight = windowHeight / 4

const styles = StyleSheet.create({
  image: 
  {
    width: windowWidth - 30,
    height: imageHeight,
    paddingTop: '2%',
    paddingBottom: '2%',
    marginTop: 25,
    shadowColor: 'transparent',
    borderRadius:10
  },
  printerNameText: 
  {
    fontSize: 20,
    textAlign: 'center',
    color: 'white',
    bottom: 0,
    left:0,
    position: 'absolute',
    marginLeft: 10,
    marginBottom: 7
  },
  printerStatusText: 
  {
    fontSize: 15,
    textAlign: 'center',
    color: 'white',
    bottom: 0,
    right:0,
    position: 'absolute',
    marginRight: 10,
    marginBottom: 10
  },
  cardCompletionViewStyle:
  {
    borderRadius: Math.round(windowWidth + windowHeight) / 2,
    width: CircleSize,
    height: CircleSize,
    borderColor: 'rgba(255, 255, 255, .3)', 
    borderWidth: 5,
    alignItems: 'center',
    position: 'absolute',
    right: 0,
    marginRight: 10,
    marginTop: (windowHeight/60)
  },
})

let StatusCodes = {0: 'Offline', 1: 'Asleep', 2: 'Printing', 3: 'Paused'}

function CardView({ printer, images })
{
  //console.log("getting passed into CardView ",  image)
  /*
  var src;
  if (images[printer.id] == false || typeof(images[printer.id]) != "string"){
    src = require('./LocalAssets/default4.png')
    console.log('src is normal stored image')
  } else {
    src = { uri: images[printer.id] }
    console.log('src is from image ', images[printer.id])
  }
  */

  return(
    <ImageBackground
      imageStyle={{borderRadius:10}}
      source= {{ uri: images[printer.id] }} // { src }
      defaultSource = { require("./LocalAssets/default4.png") }
      loadingIndicatorSource={require('./LocalAssets/default4.png')}
      style={styles.image}>
      <View 
        style=
        {{
          width: windowWidth - 30,
          height: 37,
          backgroundColor: "rgb(25, 40, 65)",
          opacity: .8,
          position: 'absolute',
          bottom: 0,
          borderBottomLeftRadius: 10,
          borderBottomRightRadius: 10
        }}>
      </View>
      <Text style={styles.printerNameText}>
        {printer.name}
      </Text>
      <Text style={styles.printerStatusText}>
        Status: {StatusCodes[printer.status]}
      </Text>
      <CompletionView text={''} style={styles.cardCompletionViewStyle} fontSize={CircleSize/3.5} textPos = {(imageHeight/5)} printer={printer} isCardView={true} />
    </ImageBackground>
  )
}

export { CardView };
