import * as React from 'react';
import { View, Text, Button, StyleSheet, Image, ScrollView, Dimensions, ImageBackground, Animated } from 'react-native';
import { useState, useEffect } from 'react'
import { Loading } from 'aws-amplify-react-native/dist/Auth';
import { lighter, darker } from './Colors.js';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const imageHeight = windowHeight / 4

const safeWindowHeight = (windowWidth > 375 ? 110 : 85)

const styles = StyleSheet.create({
  background: 
  {
    width: windowWidth - 30,
    height: imageHeight,
    paddingTop: '2%',
    paddingBottom: '2%',
    marginTop: 25,
    shadowColor: 'transparent',
    borderRadius:10,
    backgroundColor: 'rgb(25, 40, 65)'
  },
  bottomBanner: 
  {
    width: windowWidth - 30,
    height: 37,
    opacity: .8,
    position: 'absolute',
    bottom: 0,
    borderBottomLeftRadius: 10,
    borderBottomRightRadius: 10,
    backgroundColor: 'rgb(32, 51, 84)'
  },
})

function LoadingCardsView()
{
  return(
    <View>
      <LoadingCard/>
      <LoadingCard/>
      <LoadingCard/>
    </View>
  )
}

function LoadingCard()
{
  const [animation, setAnimation] = useState(new Animated.Value(0))

  const handleAnimation = () => {
    Animated.timing(animation, {
      toValue:1,
      duration: 1000,
      useNativeDriver: false
    }).start( () => {
      Animated.timing(animation,{
        toValue:0,
        duration: 1000,
        useNativeDriver: false
      }).start(() => handleAnimation)
    })
  }

  const backgroundInterpolation =  animation.interpolate({
    inputRange: [0, 1],
    outputRange:[darker['two'], darker['one']]
  })

  const bannerInterpolation = animation.interpolate({
    inputRange: [0, 1],
    outputRange: [darker['four'], darker['three']]
  })

  const animatedStyle = {
    backgroundColor: backgroundInterpolation
  }

  const bannerStyle = {
    backgroundColor: bannerInterpolation
  }

  useEffect(() => 
  {
    handleAnimation()
  }, [])

  return(
    <Animated.View style={{...styles.background, ...animatedStyle}}>
        <Animated.View style={{ ...styles.bottomBanner, ...bannerStyle }} />
    </Animated.View>
  )
}



export { LoadingCardsView };
