import * as React from 'react';
import { useEffect, useState, useReducer } from 'react'
import { View, Text, Button, StyleSheet, Image, ScrollView, Dimensions, ImageBackground } from 'react-native';
import { PauseOrCancelView } from './PauseOrCancelView';
import { listPrints, listPrinters } from './graphql/queries';

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const styles = StyleSheet.create({
    printingViewText:
    {
      fontWeight: '100',
      fontSize: 20,
      color: 'rgb(255, 255, 255)',
      position: 'absolute',
      bottom: 0,
      marginBottom: windowHeight / 11
    }
})

let StatusCodes = {0: 'Offline', 1: 'Asleep', 2: 'Printing', 3: 'Paused'}

var bool = false;

function OverlayView(props)
{
  var printerProgress = (props.printer.printerState != null ? (props.printer.printerState.progress != null ? props.printer.printerState.progress : 0) : 0)
  const [showPauseOrCancelView, setShowPause] = useState(0)

  function toggleShowPause()
  {
    if (showPauseOrCancelView == 1)
    {
      setShowPause(0)
    }else if (showPauseOrCancelView == 0)
    {
      setShowPause(1)
    }
  }
  //1 - show pause or cancel view
  //0 - show icon display (first one that appears)
  //2 - confirm cancellation

  return(
    <>
      {(showPauseOrCancelView == 0) &&
        <>
          {(bool == true) &&
            <>
              <Text 
                style=
                {{
                  fontWeight: 'bold',
                  fontSize: props.fontSize,
                  color: 'white',
                  marginTop: (!props.isCardView ? windowHeight/8.9 : ((windowHeight/4)/4)) - 5
                }}>
                {printerProgress}%
              </Text>
              <Text style= { styles.printingViewText }>{props.compeletedText}</Text>
            </>
          }
          {(bool == false) &&
            <Text 
              style=
              {{
                fontWeight: 'bold',
                fontSize: props.fontSize - 10,
                color: 'white',
                marginTop: (!props.isCardView ? windowHeight/8.9 : ((windowHeight/4)/4)) - 5
              }}>
            {StatusCodes[props.printer.status]}
            </Text>
          }
        </>
      }
      {(showPauseOrCancelView == 1) &&
        <PauseOrCancelView printer={props.printer} />
      }
      {(!props.isCardView) &&
        <Button style={{position:'absolute', bottom:0}} title="Controls" onPress={() => toggleShowPause()}/> 
      }
    </>
  )
}

export {OverlayView};
