import PropTypes from 'prop-types';
import React from 'react';
import { StyleSheet, Text, TouchableHighlight, View, Dimensions } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { Divider } from './Divider.js';
import { AppStyles as styles } from './Styles.js'

function SegmentedControl(props)
{
    const { sortItems } = props;

    const sortSheetItems = [...sortItems]

    return(
        <View style={ styles.segmentedStyle }>
            {
                sortSheetItems.map((sortItem, index) => 
                {
                    return(
                        <TouchableOpacity onPress={sortItem.onPress} key={index}>
                            <SortItem icon={sortItem.icon} text={sortItem.label} style={sortItem.style} />
                        </TouchableOpacity>
                    )
                })
            }
        </View>
    )
}

function SortItem(icon, text, style)
{
    return(
        <View style={ style }>
            {icon}
            {text}
        </View>
    )
}

SegmentedControl.propTypes = {
    sortItems: PropTypes.arrayOf(
        PropTypes.shape({
            id: PropTypes.oneOfType([PropTypes.number, PropTypes.string]),
            icon: PropTypes.any,
            label: PropTypes.any,
            onPress: PropTypes.func,
            style: PropTypes.string
        })
    ).isRequired,
}


SegmentedControl.defaultProps = {
    sortItems: [],
}

export { SegmentedControl };
